mapper.select:
select * from ( select rownum rn ,a.* from (
SELECT M.POST_NO, M.TITLE, R.ROOM_NO, M.GROUP_ID, M.REG_DATE, B.BUILDING_ID FROM MINWON M JOIN ROOM_INFO R ON M.ROOM_ID = R.ROOM_ID JOIN BUILDING_INFO B ON R.BUILDING_ID = B.BUILDING_ID
order by post_no desc
)a where
rownum <= #{pageNum} * #{amount} )b where rn > (#{pageNum} -1) * #{amount}

mapper.insert:

select NVL(max(post_no), 0) + 1 
from minwon

insert into minwon
			(
				post_no,
				title,
				content,
				category,
				GROUP_ID,
				room_no,
				visits_date,
				reg_date,
				alert_type,
				room_id
			)
		values
			(
				#{postNo}
				, #{title}
				, #{content}
				, #{category}
				, #{groupId}
				, #{roomNo}
				, #{visitsDate}


					, #{regDate}


					, sysdate


				,#{alertType}
				,#{roomId}
			)

mapper.update:
update minwon
set 
	title = #{title}
	, content = #{content}
	, category = #{category}

	, GROUP_ID = #{groupId}

	, visits_date = #{visitsDate}

where post_no = #{postNo}
