mapper.select:
SELECT * FROM ( select rownum rn , a.* FROM (SELECT building_id, building_name, doro_addr, details_addr, total_gagu FROM building_info WHERE IMDAEIN_ID = #{imdaeinId} ORDER BY 1 )a
where rownum <= #{pageDTO.pageNum} * #{pageDTO.amount} )b
where rn > (#{pageDTO.pageNum} -1) * #{pageDTO.amount}

SELECT count(*)
FROM building_info
WHERE imdaein_id = #{imdaeinId}


SELECT file_name 
FROM file_info 
WHERE group_id= #{groupId}

mapper.update:
UPDATE room_info
SET room_no=#{roomNo},
	floor=#{floor}
WHERE room_id =#{roomId}
