mapper.select:
SELECT * FROM ( SELECT rownum rn, a.* from ( SELECT bn.post_no , bn.title , bn.writer , bn.content , bn.reg_date , bn.mod_date , bn.group_id , bn.views , bi.building_id , bi.building_name , bi.imdaein_id FROM building_notice bn join building_info bi on bn.building_id = bi.building_id
ORDER BY post_no desc ) a
where rownum <= #{nowPage} * #{pageData}
) p where rn > (#{nowPage}-1) * #{pageData}

mapper.insert:

	SELECT NVL(MAX(POST_NO), 0) + 1
	FROM building_notice

insert into building_notice(post_no
, title
, writer
, group_id
, content
, reg_date
, building_id
)
values(#{postNo}
, #{title}
, '10'
, #{groupId}
, #{content}
, SYSDATE
, #{buildingId}
)

mapper.update:
UPDATE building_notice
SET views = views+1
WHERE post_no = #{postNo}

UPDATE building_notice
SET title = #{title}
, content = #{content}
, mod_date = SYSDATE
WHERE post_no = #{postNo}
