mapper.insert:

		SELECT NVL(MAX(POST_NO), 0) + 1 
		FROM building_notice

		insert into building_notice(post_no
                          , title
                          , writer
                          , group_id
                          , content
                          , reg_date
                          , building_id
                          )
		values(#{postNo}
		      , #{title}
		      , '10'
		      , #{groupId}
		      , #{content}
		      ,  SYSDATE
		      , #{buildingId}
		      )

mapper.update:
UPDATE building_notice
SET views = views+1
WHERE post_no = #{postNo}

	     UPDATE building_notice
         SET title = #{title}
            , content = #{content}
            , mod_date = SYSDATE
         WHERE post_no = #{postNo}
